import React from "react";
import axios from "axios";
import Modal from "react-modal";
class Form extends React.Component{
    constructor(props){
        super(props);
        this.state = {
            showModal: false,
            name:"",
            surname:"",
            age:"",
            address:"",
            email:"",
            errors:{}
        };

        this.handleInputChange = this.handleInputChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
        this.handleOpenModal = this.handleOpenModal.bind(this);
        this.handleCloseModal = this.handleCloseModal.bind(this);
    }

    handleOpenModal() {
        this.setState({ showModal: true });
    }

    handleCloseModal() {
        this.setState({ showModal: false });
    }

    handleInputChange(event){
        const {name, value} = event.target;
        this.setState({[name]:value});
    }

    handleSubmit(event){
        event.preventDefault();
        if(this.validateForm()){
            // Crear un objeto con los datos del formulario
            const formData = {
                name: this.state.name,
                surname: this.state.surname,
                age: this.state.age,
                address: this.state.address,
                email: this.state.email,
            };

            // Realizar la solicitud POST a tu API
            axios.post('https://localhost:7164/api/user', formData)
                .then(response => {
                    console.log('Respuesta de la API:', response.data);
                })
                .catch(error => {
                    console.error('Error al enviar los datos:', error);
                });
        }
        else{
            console.log("form invalido");
        }
    }

    validateForm(){
        let valid = true;
        let errors = {};

        if(this.state.name.length < 3){
            errors.name = "El nombre debe tener al menos 3 caracteres";
            valid = false;
        }

        this.setState({errors});
        return valid;
    }

    render(){
        return (
            <div>
                <button onClick={this.handleOpenModal}>Abrir Formulario</button>
                <Modal
                    isOpen={this.state.showModal}
                    onRequestClose={this.handleCloseModal}
                    contentLabel="Ejemplo Modal"
                >
                    {/* Contenido del formulario y botones para cerrar/enviar */}
                    <form onSubmit={this.handleSubmit}>
                        {/* ... (contenido del formulario) */}
                        <button type="submit">Guardar</button>
                        <button type="button" onClick={this.handleCloseModal}>Cancelar</button>
                    </form>
                </Modal>
            </div>
        )
    }
}

export default Form;
